namespace Ecomteck\Megamenu\Block\Menu;

use Magento\Backend\Block\Widget\Form\Container;

/**
 * Class Edit
 * @package Ecomteck\Megamenu\Block\Menu
 */
class Edit extends Container
{
    protected function _construct()
    {
        $this->_blockGroup = 'Ecomteck_Megamenu';
        $this->_controller = 'menu';
        $this->_mode = 'edit';
        parent::_construct();

        $this->buttonList->add(
            'save_and_continue',
            [
                'label' => __('Save and Continue Edit'),
                'class' => 'save',
                'data_attribute' => [
                    'mage-init' => [
                        'button' => [
                            'event' => 'saveAndContinueEdit',
                            'target' => '#edit_form'
                        ]
                    ]
                ]
            ],
            -100
        );
    }
}
