namespace Ecomteck\Megamenu\Ui\Component\Listing\DataProviders\EcomteckMenu\Menu;

use Magento\Ui\DataProvider\AbstractDataProvider;
use Ecomteck\Megamenu\Model\ResourceModel\Menu\CollectionFactory;

/**
 * Class ListProvider
 * @package Ecomteck\Megamenu\Ui\Component\Listing\DataProviders\EcomteckMenu\Menu
 */
class ListProvider extends AbstractDataProvider
{
    /**
     * ListProvider constructor.
     * @param string $name
     * @param string $primaryFieldName
     * @param string $requestFieldName
     * @param CollectionFactory $collectionFactory
     * @param array $meta
     * @param array $data
     */
    public function __construct(
        $name,
        $primaryFieldName,
        $requestFieldName,
        CollectionFactory $collectionFactory,
        array $meta = [],
        array $data = []
    ) {
        parent::__construct($name, $primaryFieldName, $requestFieldName, $meta, $data);
        $this->collection = $collectionFactory->create();
    }
}
